import React,{Component} from 'react';
import {Redirect} from 'react-router-dom';
import axios from 'axios';
import './login.css';

class Login extends Component{
    constructor(props){
        super(props)
      this.state={  
          uName:'',
        Password:'',
        data:[],
        err:''
    }
    }
    componentWillMount(){
        axios.get("https://api.myjson.com/bins/15wq8u")
        .then(response=>{
            const data=response.data;
            this.setState({
                data,
                err:'UserName or Password Wrong'
            })
            console.log(data);
        })
       }
// handleChange = e => {
//         this.setState({
//             [e.target.name]: e.target.value
//         })
//         console.log(e.target.name);
//     }
handleName(e){
this.setState({
    uName:e.target.value
})
}
handlePassword(e){
this.setState({
    Password:e.target.value
})
}
handleSubmit=(e)=>{
       
    this.setState({
        
    })
   this.props.history.push("/menu/dashboard")
    }
    render(){
        if (this.state.data === true) {
            return <Redirect to='/menu' />
          }
        return(
              <div id="loginDiv">
              <form onSubmit={this.handleSubmit} >
              <div className="loginPage">
              <div>
                 <h2 className="formLogin">Login</h2>
              </div>
                 <div>
              <label className="formLabel">UserName:</label>
              <input type="text"  placeholder="Enter User Name" className="form-control" name="uName" id="name" onChange={(e)=>{this.handleName(e)}}/><br />
            </div>
            <div>
             <label  className="formLabel">Password:</label>
              <input type="password"  placeholder="Enter Password" className="form-control" name="Password" id="pswd" onChange={(e)=>{this.handlePassword(e)}}/><br />
              </div>
            <div>
              <button type="submit" value="Login" className="btn btn-primary btn-block" >Login</button></div>
              </div>
             </form>
              </div>
        )
    }
}
export default Login;
